const allItems = $input.all();
const stats = allItems[0]?.json || {};
const categories = allItems.filter(i => i.json.category).map(i => i.json);

const apiKey = $env.OPENROUTER_API_KEY;
if (!apiKey) return [{ json: { error: 'OPENROUTER_API_KEY manquant' } }];

const systemPrompt = `Tu es Kimi, cerveau IA du projet Caballarius.
Tu rediges le RAPPORT QUOTIDIEN. Style : concis, professionnel, en francais.
Utilise des emojis pour les KPI.
Objectifs de reference :
- 2356 localites a scraper (etablissements)
- 2356 localites a enrichir (descriptions multilingues)
- Cible finale : 20 000+ etablissements, 15 000 sites pros
Reponds TOUJOURS en JSON valide.`;

const userPrompt = `Genere le rapport quotidien du ${new Date().toISOString().split('T')[0]}.

STATS: ${JSON.stringify(stats)}
CATEGORIES: ${JSON.stringify(categories)}

Reponds:
{"title": "Rapport Caballarius — JJ/MM", "sections": [{"emoji": "...", "title": "...", "content": "..."}], "kpi": {"progress_scrape_pct": 0-100, "progress_enrich_pct": 0-100, "establishments_total": N, "health": "green|yellow|red"}, "next_steps": ["..."], "one_liner": "Resume en 1 phrase"}`;

try {
  const response = await fetch('https://openrouter.ai/api/v1/chat/completions', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json', 'Authorization': `Bearer ${apiKey}`, 'HTTP-Referer': 'https://cblrs.net', 'X-Title': 'Caballarius-n8n-rapport' },
    body: JSON.stringify({ model: 'moonshotai/kimi-k2.5', messages: [{ role: 'system', content: systemPrompt }, { role: 'user', content: userPrompt }], response_format: { type: 'json_object' }, temperature: 0.5, max_tokens: 3000 })
  });
  const data = await response.json();
  const report = JSON.parse(data.choices[0].message.content);
  return [{ json: { report, stats, timestamp: new Date().toISOString() } }];
} catch (err) {
  return [{ json: { error: err.message, stats } }];
}
