const allItems = $input.all();
const dbCheck = allItems[0]?.json || {};
const openrouterOk = allItems[1]?.json?.data ? true : false;

const alerts = [];
const fixes = [];

// Check activity
const minSinceLast = parseInt(dbCheck.minutes_since_last_success) || 9999;
if (minSinceLast > 60) {
  alerts.push({ level: 'CRITICAL', msg: `Aucun job termine depuis ${minSinceLast} min â€” skills probablement crashes` });
}
if (minSinceLast > 30 && minSinceLast <= 60) {
  alerts.push({ level: 'WARNING', msg: `Derniere activite il y a ${minSinceLast} min` });
}

// Stuck jobs
const stuck = parseInt(dbCheck.stuck_jobs) || 0;
if (stuck > 0) {
  alerts.push({ level: 'WARNING', msg: `${stuck} job(s) stuck depuis >1h` });
  fixes.push(`UPDATE scrape_jobs SET status='error', error_log='watchdog: stuck >1h' WHERE status='running' AND started_at < NOW() - INTERVAL 1 HOUR`);
}

// Error rate
const errors1h = parseInt(dbCheck.errors_1h) || 0;
const done1h = parseInt(dbCheck.done_1h) || 0;
if (errors1h > 10 && errors1h > done1h) {
  alerts.push({ level: 'CRITICAL', msg: `Taux d'erreur eleve: ${errors1h} erreurs vs ${done1h} succes (1h)` });
}

// OpenRouter
if (!openrouterOk) {
  alerts.push({ level: 'WARNING', msg: 'OpenRouter API ne repond pas' });
}

const status = alerts.length === 0 ? 'OK' : (alerts.some(a => a.level === 'CRITICAL') ? 'CRITICAL' : 'WARNING');

return [{ json: {
  status,
  alerts,
  fixes,
  openrouter: openrouterOk ? 'UP' : 'DOWN',
  db: dbCheck,
  timestamp: new Date().toISOString()
} }];

const data = $input.first().json;
if (data.status === 'OK') return [{ json: { telegram: 'skipped - all OK' } }];

const token = $env.TELEGRAM_BOT_TOKEN;
const chatId = $env.TELEGRAM_CHAT_ID;
if (!token || !chatId) return [{ json: { telegram: 'skipped - no token/chatId' } }];

const icon = data.status === 'CRITICAL' ? 'ðŸš¨' : 'âš ï¸';
let text = `${icon} WATCHDOG ${data.status}\n\n`;
for (const a of (data.alerts || [])) {
  text += `${a.level === 'CRITICAL' ? 'ðŸ”´' : 'ðŸŸ¡'} ${a.msg}\n`;
}
text += `\nðŸ“Š Estab: ${data.db?.total_estab || '?'} | Content: ${data.db?.total_content || '?'}`;
text += `\nðŸŒ OpenRouter: ${data.openrouter}`;
text += `\nðŸ• ${data.timestamp}`;

try {
  const resp = await fetch(`https://api.telegram.org/bot${token}/sendMessage`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ chat_id: parseInt(chatId), text: text.substring(0, 4000) })
  });
  const result = await resp.json();
  return [{ json: { telegram_sent: result.ok } }];
} catch (err) {
  return [{ json: { telegram_error: err.message } }];
}
